import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import { ErrorBoundary } from './components/ErrorBoundary';

// Disable browser's automatic scroll restoration to prevent jumping to top on refresh
if ('scrollRestoration' in window.history) {
  window.history.scrollRestoration = 'manual';
}

// Preserve scroll position on page load/refresh
window.addEventListener('beforeunload', () => {
  sessionStorage.setItem('scrollPosition', String(window.scrollY || document.documentElement.scrollTop));
});

// Restore scroll position after page loads
window.addEventListener('load', () => {
  const savedScroll = sessionStorage.getItem('scrollPosition');
  if (savedScroll) {
    const scrollY = parseInt(savedScroll, 10);
    if (scrollY > 0) {
      // Use requestAnimationFrame to ensure DOM is ready
      requestAnimationFrame(() => {
        window.scrollTo(0, scrollY);
      });
    }
  }
});

const root = ReactDOM.createRoot(
  document.getElementById('root') as HTMLElement
);
root.render(
  <React.StrictMode>
    <ErrorBoundary>
      <App />
    </ErrorBoundary>
  </React.StrictMode>
);
